INSERT INTO jc_archives ( `id`, `arctype_id`, `title`, `seo_title`, `keywords`, `att`, `source`, `resume`, `url`, `pic`, `pic_list`, `pubdate`, `admin_id`, `hits`, `is_effect`, `is_delete`, `sort`, `time`, `end_time` ) VALUES  ('250','54','肾与肾虚','','','a','','西医所说的肾，是人体解剖学上的一个功能脏器，位于腰部脊柱两旁，左右各一。而中医所说的“肾”，不仅指解剖学上的脏器，而是指以解剖学上的肾为代表的多个系统功能的集合，涵盖了西医的生殖系统、泌尿系统、内分泌系统以及呼吸系统等多个系统的部分功能，包括了人体的生长、发育、生殖、水液代谢以及精神情志等多方面的功能。 虚','','/upload/other/image/20160215145551645431.png','[]','2016-01-21','6','0','1','0','2','1453359999','0');
