INSERT INTO jc_archives ( `id`, `arctype_id`, `title`, `seo_title`, `keywords`, `att`, `source`, `resume`, `url`, `pic`, `pic_list`, `pubdate`, `admin_id`, `hits`, `is_effect`, `is_delete`, `sort`, `time`, `end_time` ) VALUES  ('636','5','“六味七星杯”全国社区健康舞大赛到汉 火炉提前“热”起来','','','a','广场上没有亚健康','春分刚过，作为我国三大火炉城市之一的武汉，距离“热”闹的月份还有一段时间。然而，一场持续五天的健康舞大赛却让这座火炉城市提前“热”了起来。3月21日-25日，北京同仁堂“六味七星杯”全国社区健康舞大赛街道赛在武汉经济开发区、江汉区、硚口区、黄陂区和汉阳区火热开展。简约的广场舞、时尚的肚皮舞、古典的民族舞、动感的健身操、婉约的走秀表演、震撼的威风锣鼓等数十支形式各异的表演队伍给市民带来了一场视觉盛宴，同时，也掀起了一股健康养生潮。','','/upload/other/image/20160330145931482383.png','[]','2016-03-30','6','0','1','0','100','1459314088','0');
