INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('1','16','贝森路社区腰鼓队','东坡街道贝森路社区','18','63','我队成立于2006年3月，现有队员45人。主要表演腰鼓。','','','','','','','','','','','1458808308','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('2','16','贝森健身操队','东坡街道贝森路社区','12','60','我队成立于2000年10月，现有队员20人。特点：以健身为主。','','','','','','','','','','','1458808384','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('52','21','纱帽街周家河村舞蹈队','纱帽街周家河村','0','0','　　在武漢西南隅，有一個美麗的村庄。宁靜而祥和，犹如镶嵌在漢南区蚂影河边的琥珀，又仿佛是大地留給季节的情诗，時而质朴，時而华彩 ，其實，任何美好都離開善的秉承与操持。周家河村能有現在美麗的景象 ，离不开全村倡导環保治村的理念：提倡水果种植，釆摘，垂釣，等綠色經濟为主导，生产模式,让村民首先享受良好的经济环境，继而充实文化生活，因此多次受到上级领导表彰，而且被市政府评选为武汉市十大魅力村庄之一。 
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('4','16','阳光舞蹈队','东坡街道清水河社区','13','60','成立于2009年，有队员20余人。队员年龄层次丰富，从40多岁至70岁。舞蹈节目故事性和感染力强。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('5','16','双新路社区舞蹈队','东坡街道双新路社区','12','58','双新路社区舞蹈队成立于2014年1月，曾参加过“2015年四川电视台耀武扬威创意舞蹈预热赛”、成都市老龄委比赛、2015年成都市老龄委体协比赛、2016年东坡街道总工会游园活动等，获得优秀奖。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('6','16','易诚快乐之舞健身队','苏坡街道培风路社区','20','55','易诚快乐之舞健身队是由金阳.易诚国际小区的健身操爱好者为主，于2012年7月自发组织的健身操队伍，从成立之初的几个人发展为至今有固定成员八十余人，规模不断壮大，形成了有领队、有领舞的核心团队，有组织、有计划、按时参与的活动；队员的年龄在三十多岁至七十余岁之间，平均年龄约五十六岁。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('7','16','清波社区舞蹈队','苏坡街道清波社区','12','59','清波社区舞蹈队于2003年成立，至今已经十二年，曾经多次参加市上区上的文艺比赛，你说过3次优秀奖，1次创意奖。','','','','','','','','','','','1458808742','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('8','16','微微艺术团','苏坡街道','20','58','该团成立2013年3月，参加青羊区2015文化四季风劲舞暖冬话动获三等奖，该节目被推荐到市上参加耀武杨威广场舞比赛成功晋级，十一月在四川电视演播厅进行节目录制，于2016年元月1日晚在妇女儿童频道成功播出。','','','','','','','','','','','1458808769','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('9','16','文化宫舞友队','东坡街道清水河社区','25','60','团队自2009年创建以来，每月定期进行舞友交流活动：连续举办两届成都市群英联谊会，举办三届圣诞化妆舞会。为成都市专业和业余退休的文艺爱好者搭建平台。深得大家的青睐和赞扬。被称之为；快乐之家。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('10','16','万家湾舞蹈队','苏坡街道培风路社区','12','65','万家湾舞蹈队于1999年成立，现在有30多人，曾参加成都市腰鼓比赛，青羊区合唱，舞蹈比赛，自及创作小品，快板。快板“美丽社区，我的家”荣获青羊区创作大赛二等奖，表演唱“逛蓉城”荣获青羊区自唱节目比赛三等奖。','','','','','','','','','','','1458808862','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('11','17','北社区空竹队','黄忠街道金沙公园北社区','20','65','北社区空竹队自成立以来，积极参加社区组织的各种活动，其活动受到了社区居民的热烈欢迎，空竹的表演非常有观赏性，难度很大，此次活动还有很大的发展潜力。','','','','','','','','','','','1458808928','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('12','17','太乙龙珠队','黄忠街道金沙公园北社区','20','55','太乙龙珠队自2015年成立以来，参加街办区市组织的各类文体活动，培养了社区太乙龙珠爱好者的兴趣爱好，进一步丰富了社区文化生活，一是健身，二是观赏，为社区的文化生活增添人色彩，现社区共有30余人参与了此项活动。','','','','','','','','','','','1458808964','0','1');
