INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('50','20','东街社区舞蹈队','东街社区','34','0','东街社区舞蹈队是一支自创的民俗舞蹈，新繁棕编是四川省成都市新都区新繁镇的汉族传统手工艺品之一，是国家级非物质文化遗产—新繁棕编。这支舞蹈曾获得“成都市新市民风采大赛”一等奖','','','','','','','','','','','1458810765','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('51','16','丽都玉园舞蹈队','东坡街道东坡路社区','16','62','东坡路社区丽都玉园舞蹈队成立于2013年10月，组员共计16人，平均年龄62岁，他们坚持面向社会、面向群众、面向社区的宗旨，为社区老年群众作演出服务，参加过市、区以及社区组织的各种宣传活动，不计报酬，总是热情的态度。宋惠儒为人热心和对艺术的执著，也是让大家动容的一个重要原因。并担任东坡路社区老年人协会副会长，“她很有爱心，一直以来，几乎天天前来义务教大家跳舞和唱歌。做好事很容易，难的是坚持，但她做到了。是一支值得我们学习的团队。','','','','','','','','','','','1458808570','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('53','21','军山街蒲潭社区舞蹈队','军山街蒲潭社区','30','46','队伍由30名爱好舞蹈的居民组成。她们年龄最大的46岁，最小的24岁。她们热爱生活、热爱舞蹈。她们牺牲了平时的休息时间，不怕辛苦、客服困难。就是为了能编排出精彩的节目。她们编排出的健身排舞《太空漫步》还在武汉市第二届广场舞大赛中取得了第三名的好成绩。','','','','','','','','','','','1459475820','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('54','21','金色港湾社区舞蹈队','沌阳街金色港湾社区','0','56','　　金色港湾社区舞蹈队是一支平均年龄在56岁的退休人员组建而成，她们没有舞蹈基础，对舞蹈也没啥理解，就凭着一腔热情和不懈的努力，在队员的互帮互助下，经过不断的练习和磨练，已经成为一个具有表演性的群体。通过舞蹈健身的方式，赋予健身以娱乐性，提高了居民群众的健身热情，充分展示了社区老年人的精神风貌。她们所带来的小苹果这支舞蹈展现了青春的活力，多次参加市级比赛获得优秀奖，参加大型广场舞比赛获得二等奖，参加沌阳街广场舞获得一等奖等。','','','','','','','','','','','1459475865','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('55','21','慧乡文艺队','邓南街捞子湖村','20','0','　　近几年，捞子湖村慧乡文艺队队伍从当初的几个人迅速增多到二十几个，从自娱自乐、强身健体发展到登台为更多身边的老百姓演出。每当夜幕降临，彩灯亮起，优美的音乐便在小区里响起，一群身着亮丽服饰妇女们聚在一起，表演舞蹈，排练文艺节目，有腰鼓、扇子舞、手绢舞，热闹非凡。通过自创自编自演，并用群众喜闻乐见、通俗易懂的形式表演，受到了邻里乡亲的好评。2012年我们受区老龄活动的邀请，参加汇演，精心排练，腰鼓舞“开门红”得到了满场的好评。2013年由我村代表邓南街参加纪念“三八”国际劳动妇女节103周年，“我健康、我美丽、我快乐”的比赛，她们用自己的热情及生动的表演打动了全场的观众和评委，最终一曲“新龙船调”广场舞喜获金奖。通过一系列的活动，反映当代农民积极向上，热爱生活的精神风貌。','','','','','','','','','','','1459475926','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('56','21','乌金社区舞蹈队','东荆街乌金社区','24','0','　　在东荆街乌金社区有这样一支舞蹈队，24名队员中有农民、有社区居民，也有下岗职工。从不懂音律到舞艺渐长，她们的表演越来越精彩；她们坚持自己的爱好与梦想，把健康与快乐带给大家；她们怀着对新农村建设的美好愿景，舞出了美好人生，她们就是乌金社区舞蹈队。','','','','','','','','','','','1459475957','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('57','21','月亮湾舞蹈队','纱帽街','30','50','　　《摆手太阳鼓》是月亮湾社区以企业下岗职工为主，30名居民自发组织，平均年龄在50岁以上的队伍，她们个个能歌善舞，热爱文艺，热爱生活，曾多次荣获“广场舞比赛”一等奖。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('58','21','宁康园社区舞蹈队','沌口街宁康园社区','43','60','　　曾获奖项：沌口街举办的《沌口街纪念中国人民抗日战争暨世界反法西斯战争胜利70周年大型文艺汇演》。宁康园社区广场舞表演队经过认真的组织和排练，选送了舞蹈《五星红旗飘起来》，获得了三等奖。','','','','','','','','','','','1459476037','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('59','21','万家湖社区舞蹈队','沌口街万家湖社区','0','55','　　万家湖社区舞蹈队由居民自发组建于2000年，平均年龄55岁，她们于2012年参加武汉市万人排舞，荣获三等奖。今天她们怀着对党的感恩编排了一曲土家族舞新龙船调，展现了她们对幸福生活无比的愉悦心情。','','','','','','','','','','','1459476357','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('60','21','江大园社区舞蹈队','沌阳街江大园社区','0','58','　　有这样一群人，她们热情洋溢，她们朝气蓬勃，她们用激扬的舞姿来抒写青春之歌……有这样一个集体，她温暖如家，她凝聚团结，她用不息的努力来追逐梦想……
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('61','21','火舞春秋舞蹈队','湘口街','20','46','　　湘口街道火舞春秋舞蹈队于2013年3月8号成立，有队员20多名，平均年龄46岁，年龄最小的38岁，最大的已有53多岁。她们是来自各社区，村的文化骨干和文艺爱好者，个个能歌善舞，队里没有专业教练，没专业舞蹈队员，大家为一个共同的爱好走到一起，闲暇之余，她们聚集在一起切磋舞艺，相互学习，把学到的新节目无私地传授给本村，社区的业余舞蹈爱好者。并多次参加街道，社区举办的广场舞比赛。 在火舞春秋舞蹈队的带动下，现3个社区，10个大队，1个村都成立了文化娱乐队伍。目前，全街共开展文化演出活动10余场，群众文化生活实现全覆盖，满足了群众的精神生活需求，营造了浓厚的群众文化氛围。全街呈现出“村村大舞台，户户踏歌声”的和谐景象。该队曾在2014年湖北省首届国际舞（三步踩）邀请赛荣获湖北地区集体二等奖。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('62','22','民航舞蹈队','汉兴街民航里社区','12','63','　　该团队于2008年成立，在社区党组织的领导下，积极参加区、街的各项文化活动，多次参加比赛和演出，在参加江汉区群众文化比赛中，取得了较好成绩，获得了第三名。时装秀节目虽然以时装走秀为主线，但经过团队成员集体智慧，编演出结合时装、礼仪示范、民族韵律动作于一体的新方式，深受居民好评。','','','','','','','','','','','1459476725','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('63','22','南航舞蹈队','汉兴民航里社区','15','60','　　该团队于2006年成立，一直为南航公司的活动团队，经常参加南航公司及旗下公司举办的文化活动，由于节目时更时新与时俱进，成为南航公司文化团体中最活跃的一支队伍，在与社区共建活动中，经常性参与社区联欢活动，以质量高、水准好的舞蹈赢得了党员、居民的喜爱。','','','','','','','','','','','1459476770','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('64','22','社区腰鼓队','北湖街正街','20','55','　　正街社区腰鼓队，成立于2010年11月，现有成员50人，都是辖区退休的五十岁以上的老年人，年龄最大的70岁。她们坚持定期开展活动，并以“舞动旋律、健康永驻”为活动理念。
