INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('66','22','单洞新村小学','新华街','22','36','单洞新村小学教师舞蹈队成立于2010年。为了参加江汉区教育局组织的各项纪念三八妇女节、七一建党节和十一国庆节而组建，教师们利用课余时间在专业舞蹈教师的指导下训练体型和基本功以及排练舞蹈。我们这支业余舞蹈队多次参加江汉区教育局组织的大型比赛活动，每次都能取得不俗的成绩，先后有原创节目快板舞蹈《2008》、歌舞《绣红旗》和《感谢春天》等荣获一等奖。我们在歌声中愉悦身心，在舞蹈中诠释对生命、对生活、对春天、对一切美好事物的热爱与向往。','','','','','','','','','','','1459476877','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('67','22','常二社区胜玲艺术团','汉兴街常二社区','20','53','常二社区胜玲艺术团于2006年9月17日正式成立，团队人数48人，平均年龄53岁。我们这支队伍团结友爱、积极向上，曾经参加过湖北电视台举办的社区大舞台，荣获优秀奖。2012年参加社区健身操比赛获得三等奖。','','','','','','','','','','','1459476903','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('68','22','红领巾艺术团','江汉区','24','13','武汉市江汉区红领巾学校艺术团创建于1969年，1970年被市政府确定为唯一对外开放学校，成为武汉市第一家在外交部备案的外事接待学校。叶剑英元帅曾亲切接见学校师生，学校先后接待包括日本中曾根首相、西德科尔总理、罗马尼亚国家元首及美国、英国、法国、泰国、新加坡等三十多个国家的政府高级代表团和旅游团体万余人。                                                                                                                                                                                                             
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('69','22','大兴路幼儿园舞蹈队','大兴路幼儿园','15','25','本团队成立于2011年，舞蹈《征途》曾荣获江汉区建党九十周年比赛金奖。团队成员是一群青春活泼的幼儿园老师，我们热爱生活，积极向上，舞蹈让我们更加自信、从容。我们倡导“舞动激情,美丽人生”的舞蹈理念。我们朝气蓬勃，积极向上，希望能带给大家温暖的阳光和满满的正能量。','','','','','','','','','','','1459477100','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('70','22','江汉区天地之韵艺术团','江汉区','24','55','天地之韵艺术团是在2007年组建成立的。目前共有正式队员30人，平均年龄近50岁，队员虽然来自社会的不同岗位，但队员心中都有一个“梦”——那就是对生命的热爱，对生活的憧憬，对幸福的向往和对舞蹈的执着，就是怀揣的这样的梦，走到了一起。“阳光舞蹈，舞蹈阳光”是我们追寻的目标。几年来，我们参加国内艺术比赛，先后获得江汉区第二届群众广场舞大赛“特别奖”，武汉市第二届群众广场舞大赛“优秀创作奖”，湖北省第二届群众广场舞展演金奖，香港“紫荆花”舞蹈大赛金奖，“华之泰”杯我要上央视老年春晚决赛一等奖，参加2016年湖北省、武汉市老年春节晚会。
