INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('72','24','翠微新城舞蹈队','翠微街归元社区','20','58','　　有这样一个集体，她温暖如玉，她凝聚团结，她用不懈的努力来追逐梦想……她就是翠微新城舞蹈队。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('73','24','黄鹤飞舞舞蹈队','二桥街玫瑰西园','20','58','　　我们这支队伍，成立比较短，是响应第二届广场舞蹈大赛才建立的，但这支队伍，团结一心，坚持学习，不断完善自我，把握来之不易的学习机会，一路取得了广场舞大赛进入前三名，参加武汉市民政局迎春晚评为优秀舞蹈奖、湖北首届广场舞大王擂台赛“冠军”，参加了春晚录制，获得汉阳区“理想好声音”荣誉证书，今年一共获得了八大奖项。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('74','24','红叶艺术团','二桥街知音东苑社区','30','60','　　汉阳文化馆红叶艺术团，成立8年来。在省、市舞蹈大赛中多次获得金、银奖，深受广大舞蹈者的喜爱和欢迎。2015年在汉阳区广场舞比赛中获得金奖。参加武汉市第二届舞蹈比赛中“咱们工人有力量”获得银奖。舞蹈：咱们工人有力量，阵容宏大，气势磅礴。整个舞蹈动作，是从各行业的劳动中提炼了好多劳动生产的动作和机器运行的动作，充分展现了产业工人的正能量。老师在排练的过程中严格要求，动作跳起来整齐划一，排练中一丝不苟，团员们不怕累、不怕苦，为自己的爱好和宣传正能量作出了一份贡献。','','','','','','','','','','','1459839655','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('75','24','凤凰艺术团','建桥街显正社区','22','55','　　团队成立1994年，至今已有二十多年，参加过无数次比赛和表演，得过无数次奖。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('76','24','汉阳公园舞蹈队','建桥街钟家村社区','20','55','　　汉阳公园舞蹈队成立于2013年，曾参加过新城阳光国际广场舞杯广场舞大赛，荣获了三等奖，在月湖街琴台华府杯广场舞大赛中获得了二等奖。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('77','24','青龙艺术团','江堤街汉博社区','24','58','　　本队成立于2010年，获得市区一、二等奖，社区大舞台季冠军，参加进京，央视七台，首届百姓春晚录制《松花江》，本团队百分之九十人员都是退休职工，因退休后大家呆在家里无事天天坐在麻将桌上，一、影响家庭；二、影响身体，常发生邻居之间不和谐，成立了艺术团后，大家和谐又开心，更重视锻炼身体，这是我团共同之声。','','','','','','','','','','','1459839766','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('78','24','新新舞蹈队','江堤街新新社区','20','45','　　新新社区舞蹈队成立于2012年8月，该团队是由社区一群热爱生活、热爱舞蹈的妇女同志自发组织的业余娱乐团体，由最初的10余人发展到40余人，平均年龄45岁。除了平常健身自娱自乐外，她们积极参加社区邻里节、“三八”妇女节、庆国庆、纳凉晚会、中秋晚会以及社区迎检等活动，取得了不俗的成绩，为社区争得了荣誉，展示了社区居民群众精神面貌，在丰富居民业余文化生活，促进社区和谐发展起到重要作用，获得了社会各界广泛好评，《土风族摆手舞》创作于2014年，是土家族古老的传统舞蹈、服装、动作具有鲜明的特点，主要取材于土家人生产生活，日常生活，反映了土家人的生活风俗，是土家族人缅怀祖先，追忆民族迁徙，再现田园生活的大型舞蹈史诗。','','','','','','','','','','','1459839799','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('79','24','威风锣鼓队','新管委会太子观澜社区','32','55','　　该团队成立于2011年，是一个新组建的团队，团队成员由零起步，热爱群众性的文艺活动。曾参加过多次区、市级比赛，2013年参加汉阳区“永丰杯”第十届武汉市迎春锣鼓比赛获得金奖；2014年参加湖北广电生活频道《社区大舞台》第一季周冠军，以及其它奖项。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('80','24','红炫风舞蹈队','五里墩街五龙里社区','20','54','　　我队成立于2015年广场舞比赛，参加区级、社区组织的各项比赛，取得较好的成绩，夺得一、二、三等奖，团队以和谐、健康向上、积极参与为宗旨，时间不长，但大家热情高，希望在2016年更上一层楼，积极参加一切社会活动，舞出健康。','','','','','','','','','','','1459839865','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('81','24','随风艺术团','五里墩街五琴里社区','20','54','　　汉阳区随风艺术团成立于2006年3月。舞蹈“布衣”参加全国中老年大赛获得金奖。舞蹈“兵之歌”参加武汉市首届广场舞比赛获金奖，舞蹈“青青木叶情”今年获得武汉市第二届广场舞金奖。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('82','25','圆梦舞蹈健身队','前川街','20','52','　　圆梦舞蹈健身队是在黄陂老年大学统一领导下的一支正规优秀的团队，她自2015年成立以来，经过不断的努力，团队日益增大、增强、水平不断提高，经常参加各种文艺演出和比赛，并有突出的成绩。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('83','25','黎元洪云姿美舞蹈队','前川街','12','48','　　黄陂区黎元洪云姿美舞蹈队成立于2011年，队员平均年龄46岁左右，队员90余人，近几年来，排练了《踏歌起舞的中国》《祖国的好江南》《十送红军》等一批优秀节目舞，先后参加了区委举办的重阳节慰问，纪念抗战胜利70周年，庆祝十八大文艺汇演等大型活动，其中舞蹈《祖国的好江南》荣获武汉市电视台举办的恒大龙城杯广场舞大赛金奖和武汉市汤包TV网络人气奖，充分发挥了广场舞文明健康的积极作用，得到了最高社会各界的欢迎和好评。','','','','','','','','','','','1459841845','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('84','25','老年大学舞蹈班','前川街','19','53','　　黄陂区老年大学舞蹈班，开班于2004年春季学期，多年来努力开展学为结合活动，积极回报社会，创作了很多优秀节目，参加了市、区各种形式的展示比赛。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('85','25','老年大学模特一队','前川街','20','51','　　本团队2009年成立，现有队员100多人，聘请专业模特老师教学，教学内容有模特基本步及形体训练、服饰展示 ，具有相当的趣味性，队员大多是离退休人员和少数居民，大部份队员具备一定学历，曾先后多次参加区委及社区组织的文艺活动，并出演香港、韩国、南阳等地的文化交流活动，取得一定成绩。','','','','','','','','','','','1459841943','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('86','25','木兰艺术团','前川街','20','58','　　黄陂区前川街木兰艺术团成立于2006年，这是一支艺术素质较高的业余团队，该队的节目贴近群众、贴近生活。连省、市区乃至全国屠获大奖，如舞蹈《洪湖情》《琴台古韵》自编的民族,“老汉推车舞”在全区舞蹈获第一名；在湖北省中老年才艺比赛中获二等奖；在省、市获楚天群星，黄鹤群星奖。自编的歌颂黄陂区全国道德模范孙东林的湖北大鼓《信义兄弟》获全国网络春晚（2013年）“十佳节目”。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('87','25','晨曦舞蹈队','前川街','18','48','　　黄陂区晨曦舞蹈队正式成立于2015年6月。本着操练身体，陶冶情操的宗旨，认真学习拉丁舞，民族舞蹈，刻苦训练，创作排编出优秀的节目，积极参加区内外各类演出活动。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('88','25','风采舞蹈队','前川街','20','50','　　黄陂区健身舞蹈协会风采舞蹈队自2015年6月12日成立以来，风采舞蹈队不断加强交流和学习。7月25日参加了2015年全国中老年广场舞组委会推荐参加了去香港举办的“舞动夕阳，共圆梦想”2015年全国中老年广场大赛展演活动，取得了专家的一致好评；9月16日由区曲艺家协会、区老年大学和区健身舞蹈协会共同举办的纪念抗日战争胜利七十周年文艺晚会，并多次送文艺下乡，文艺到企业，由区文化馆推荐参加了武汉市第二届群众广场舞大赛和湖北省第十届“中国人寿杯”中老年才艺大赛等等活动。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('89','25','金色阳光艺术团','前川街道民安社区','16','51','　　金色阳光艺术团成立于2007年，我们来自群众，扎根社区，是一支快乐、时尚、充满活力艺术素养较高的业余团队。团队的精神是敢于创新，勇于挑战、贴近群众、贴近生活。九年的成长，九年的荣耀，硕果累累。多次参加全国省、市大型舞蹈比赛。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('90','25','老年大学春暖花开舞蹈队','前川街','16','45','　　黄陂区老年大学春暖花开舞蹈队成立于2014年3月，2015年12月参加了区老年大学举办的舞蹈节目大赛，荣获二等奖。为了使该节目不断完美靓丽，舞蹈老师对动作进行了反复的修改和彩排，把舞蹈的姿态美和造型美变得更加靓丽。','','','','','','','','','','','1459842192','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('91','25','老年大学模特二队','前川街','20','51','　　本团队2009年成立，现有队员100多人，聘请专业模特老师教学，教学内容有模特基本步及形体训练、服饰展示 ，具有相当的趣味性，队员大多是离退休人员和少数居民，大部份队员具备一定学历，曾先后多次参加区委及社区组织的文艺活动，并出演香港、韩国、南阳等地的文化交流活动，取得一定成绩。','','','','','','','','','','','1459842223','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('92','26','蓝宝石舞蹈队','宝丰街宝地社区','20','52','　　该舞蹈队是由辖区居民文艺骨干组成，他们利用业余时间，长期坚持跳舞健身、以此来愉悦身心，提高身体素质。该队成立于2008年，曾多次代表宝丰街和硚口区参加区级、市级的舞蹈大赛获得各种奖项。','','','','','','','','','','','1459846662','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('93','26','长寿舞蹈队','军院社区','20','62','　　该队成立于2011年，其成员均来自于军队的退休干部，平均年龄62岁，一色的男子汉，2012年在武汉市民政局举办的文艺汇演中，该队参演的舞蹈《翻身农奴把歌唱》荣获二等奖；2014年在湖北省老年人才艺大赛中，该队参演的歌伴舞《男子汉去飞行》荣获二等奖。','','','','','','','','','','','1459846719','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('94','26','长寿舞蹈队','军院社区','20','62','　　该队成立于2011年，其成员均来自于军队的退休干部，平均年龄62岁，一色的男子汉，2012年在武汉市民政局举办的文艺汇演中，该队参演的舞蹈《翻身农奴把歌唱》荣获二等奖；2014年在湖北省老年人才艺大赛中，该队参演的歌伴舞《男子汉去飞行》荣获二等奖。','','','','','','','','','','','1459846757','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('95','26','水厂路幼儿园舞蹈队','韩家墩街四新社区','20','0','　　舞蹈队从1995年成立以来,参加全国省,市,区各种形式民族舞比赛,获全国铜奖,获省市区二等,三等若干次,团队特点是各种民族舞,','','','','','','','','','','','1459846797','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('96','26','海燕舞蹈队','汉水桥街海工社区','20','65','　　 海工社区海之蓝艺术团志愿服务队2009年由一班爱好运动、舞蹈、表演的中老年人自发组成,目前海工社区海之蓝艺术团志愿服务队大多数为退休老人，当中也有一些中年人及年轻人，他们都是广场舞爱好者，年纪最大的70多岁，年纪最小30出头，老中青的结合，使这些退休老人更为开心，更充实，更幸福。这支志愿服务队，依托文艺基地，以“我们的中国梦·社区文化进万家”为主题，长期深入居民群众中开展文艺志愿服务，为社区创建五星级幸福社区、省和谐社区、市书香社区都作出了贡献，年演出25场，每晚7：00时至8：30分在大学第一田径场开展艺术交流活动。为社区居民群众文化生活增添了浓厚文艺氛围。先后参加了区、街、社区、电视台举办的各类比赛，取得了可喜成绩。','','','','','','','','','','','1459846830','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('97','26','咏梅舞蹈队','六角亭街慈善社区','23','55','　　咏梅舞蹈队成立于2001年，队长陈杏梅具有国家级社会体育指导员资格证书，带领团队多次参加各类大型活动表演，并多次在各类比赛中获奖。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('98','26','依然美时装模特队','硚口区女干部','23','62','　　昔日巾帼不让须眉，今日时尚引领风骚。这就是硚口区老干部艺术团依然美时装队。她们来自硚口区各条战线的副处及以上干部,她们的平均年龄62岁，她们中有原街道办事处党委书记、人事局长、宣传部长、妇联主任、城管书记、还有法官、工商局长等。在过去硚口区两个文明建设中，做出过积极的贡献。如今她们虽已退休，仍不断学习舞蹈、形体、时装模特表演，经常为街道居民、残疾人、地区军民、带去很多优秀的节目，并受到各方的赞赏。在武汉市模特大赛中曾多次获表演一等奖、最佳组织奖；特别是2015年‘千人旗袍’秀活动中获最佳表演奖、最佳组织奖；在香港比赛中【茉莉花】获舞蹈一等奖、最佳组织奖。','','','','','','','','','','','1459846962','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('99','26','天之韵舞蹈队','宝丰街站邻园社区','20','47','　　该舞蹈队是辖区文艺骨干和有文艺专场的幼师和小学教师组成，队伍成立于2010年。曾多次代表硚口区参加全省民族舞大赛，全市广场舞大赛，并获得金奖。','','','','','','','','','','','1459846997','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('100','26','喜洋洋艺术团','荣华街玉带社区 ','20','60','　　我们的团队成立于2003年12月，为了丰富社区文体活动，在社区书记主任的号召下，由社区内中老年妇女和文艺爱好者组成，现有十支队伍，有成员480人，队员平均年龄60岁左右，这是一支充满朝气与活力的队伍，在社区的大力支持和领导下，在杨桂荣队长的有力带动下，在全体团员的共同努力下，短短几年就获得了较高荣誉，同时队伍也在不断发展壮大和增加特色，她们这支队伍多次代表硚口区、荣华街参加新春舞龙大赛。其中突出的是2007年3月在硚口人民广场代表荣华街新春舞龙大赛获金奖；2008年2月参加新春舞龙大赛获银奖；2009年2月代表硚口区在汉阳大禹神话园参加永丰杯新春大赛获优秀奖；2011年2月参加硚口区新春舞龙夺标赛获金奖；2011年11月代表荣华街参加我健康我快乐健身排舞比赛获金奖；2012年2月参加区新春舞龙大赛获金奖；2013年2月参加“越秀杯”硚口区军民共建新春舞龙大赛获得银奖；2014年2月参加市腰鼓大赛获得银奖；14日参加区迎新春舞龙大赛获得金奖；10月在浙江横店，参加全国老年大妈比赛获得金奖第一名；2015年春节“中国梦.春晚梦”第五届中老年网络春晚演出，舞蹈《青花瓷》荣获得金奖，2015年3月2日，腰鼓队参加武汉市第二十一届迎春舞龙大赛暨第十二届腰鼓比赛获得金奖；3月3日参加区新春舞龙比赛获金奖，以后这支队伍将在全民健身的热潮中不断学习和发展壮大。','','','','','','','','','','','1459847035','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('101','26','快乐舞动舞蹈队','宗关街发展社区  ','20','58','　　发展社区“快乐舞动”舞蹈队成立于2003年，现已有队员30余人。这支舞蹈队平均年龄有58岁，他们常年活跃在社区的文化广场、幸福长廊，积极参加社区组织的各项志愿者服务活动。   
