INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('103','50','侨友艺术团','渔人码头广场','20','0','广州侨友艺术团成立于1994年11月，22年来，艺术团坚持走文艺为人民群众服务之路，创作、学习、改编了一批歌颂党和祖国、歌颂社会主义的歌舞节目，许多节目在全国、省、市的各种群众文艺汇演、舞蹈和体育大赛中获得优异成绩。舞蹈《放马山歌》反映我国大西北马场生活的情景，展示了牧马人热情奔放、豪爽豁达的风釆。该节目在“广州市第十届美在金秋艺术大赛”中荣获金奖。','','','','','','','','','','','1460101892','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('104','50','岭南新世界舞蹈队','渔人码头广场','18','0','我是广州岭南新世界舞蹈队，我们队历年来参加各项活动的比赛和演出都获得第一名第二名好的成绩，我们还在广州大剧院演出获将。今天表演的唱响中国梦是苗族广场舞，这支舞蹈是国家一级教练给我们编排指导的，队形，舞姿都体现苗族儿女用欢快，奔放对祖国的热爱和歌颂。','','','','','','','','','','','1460101932','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('105','50','洛浦丽江花园文协快乐舞蹈队','渔人码头广场','20','0','洛浦丽江快乐舞蹈团成立于2001年，团队年龄最大的是73岁，平均年龄62岁，先后获得多项荣誉，广州市第十二届“美在金秋”中老年风采大赛（金奖）。由市舞蹈家协会及广州市文学艺术界联合会举办广州市第二届中老年舞蹈大赛获得金奖，参加CCTV中央三《舞蹈世界》全民星比赛,第三期第一名.','','','','','','','','','','','1460101955','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('106','50','合星艺术团','渔人码头广场','14','0','合星艺术团成立2012年,先是太极,广场舞,2015年加入协会成员己有九十多人,参加过20l5美在金秋（山路十八弯）深受老师们喜爱,参加过协会举办的惠东表演,我们全体成员还在继续努力！','','','','','','','','','','','1460101976','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('107','50','御金沙艺术团','渔人码头广场','20','0','御金沙艺术团成立于2013年，我们自始至终参加了市老年协会组织的每一次演出和比赛，2015、6月的“欢天喜地”和11月的“欢聚一堂”均获得优胜奖。我们还参加了本小区的才艺大赛和其他一些对外演出。本月28日我们参赛的是广场舞”站在草原望北京”，我们的目的是重在参予，向其他团队学习。','','','','','','','','','','','1460102000','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('108','50','南村镇文联雅居乐南雅艺术团','渔人码头广场','20','0','南村镇文联雅居乐南雅艺术团28号表演自创自编广场舞逛新城。我团成立6年了，到现在发展到108人。我团现有2个舞蹈队。一个时装队，一个太极队。2014年荣获省，市，区14个大奖。2015年荣获全国，省，市，区20个大奖。中国华文奖组织金奖，最佳风采奖，太极荣获广东省教育促进会一等奖，二等奖。广东省健身秧歌优胜奖，区舞蹈大赛铜奖，地产广场舞比赛第三名等。2015年参加全国，省，市，区的公益活动89次。创新节目14个。这些成绩都是在市中老年文化艺术协会的正确领导和支持，关心下取得的。也是在我们各团队的帮助和支持下所获得。谢谢所有关心，帮助和支持过我们团的的领导和老师们。','','','','','','','','','','','1460102028','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('109','50','广州邮政鸿雁艺术团','渔人码头广场','18','0','广州邮政鸿雁艺术团成立于2008年，团队成立八年以来在全体队员的不懈努力和各级领导的关心支持下，队伍的整体水平逐年提高，除有学习节目，还有了自己创作编导的节目。其中自己创作的古典舞“爱莲说”和“满江红”分别获得2013年和2014年《美在金秋》老年人风采大赛的优秀节目奖。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('110','50','星光红叶管乐团舞蹈队','渔人码头广场','18','0','广州星光红叶管乐团舞蹈队，队员来自于广东老干部大学在读学员。他们对艺术的追求孜孜不倦，为了提高艺术的素养，除了参加学校举办的音乐、舞蹈、钢琴、民族乐器、摄影、图片后期制作等等各类学习外，并积极参加省、市政府及文化部门、老龄委举办的各种大型文艺活动，并多次受到了主办单位的表扬和奖励。我团还到解放军(武警部队)慰问演出，受到了部队广大指战员们的热烈欢迎！','','','','','','','','','','','1460102114','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('111','50','岭南新世界美丽舞蹈队','渔人码头广场','20','0','岭南新世界美丽舞蹈队是一支刚组建的队伍，这些老年人都是来自五湖四海，我们头一次参加老年协会举办的健身操大赛，主要是像其它舞蹈队的姐妹学习','','','','','','','','','','','1460102187','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('112','51','圆梦艺术团','文化广场','17','0','《圆梦艺术团》于2015年6月在华南新城小区成立的。队员们的平均年龄六十岁。圆梦艺术团成立没多久就先后参加了广州市“美在金秋”大赛和中国大妈网的比赛。“美在金秋”参赛时还上了广州电视台的头条；《中国大妈》和《火火的姑娘》分别获得了中国大妈网“同曲学舞青春魅力奖”和“最具人气奖”；2016年1月参加了《悦舞悦年轻》总决赛获得了《优秀表演奖》。圆梦艺术团自成立以后参加了不少比赛和公益慈善活动，受到了好评。','','','','','','','','','','','1460102549','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('113','51','西海英姿艺术队','文化广场','12','0','我们西海英姿队是成立不到两年，由汇海，星海，瀚海花园几个小区的退休干部，社区居民组成。虽然成立时间不长，但已初露锋芒，我们正2015年民生银行举办的，活力民生，舞动羊城，舞蹈大赛中进入前十名并获得优胜奖，在今年三月初白云区金沙街举办的，美丽金沙凤舞滨江的比赛中获得一等奖。我们的参赛节目是，康巴情。我们的目的是众在参于，提高自己。谢谢。','','','','','','','','','','','1460103132','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('114','51','黄花康乐艺术团','文化广场','18','0','黄花康乐艺术团成立于2009年，是由一群热爱舞蹈艺术的大妈大叔组成。几年来在省妇联和街道文化中心的大力支持下，艺术团本着文艺为社会，为公益，为老年人服务的宗旨，精心把团队打造成一个团结互爱，健康快乐的艺术团队。几年来我们多次参加省市区街道等部门组织的比赛和文艺演出，均取得了较好的成绩，获得了好评。本次参赛的节目是《圆梦中国》，希望得到大家的喜欢。','','','','','','','','','','','1460103190','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('115','51','红舞鞋艺术团','文化广场','18','0','原名红舞鞋拉丁舞俱乐部，成立至今已有十一年，一直致力推广和发展拉丁舞，俱乐部的拉丁艺术舞“辉煌的旋律”和“春花秋月情”分别荣获美在金秋中老年舞蹈大赛的铜奖和优秀奖以及省市区各比赛名列前茅。今年为了更好地发展多元化的舞蹈，改名为红舞鞋艺术团，今次参赛的扇舞（我的祖国）首次登台接受评委的检验。','','','','','','','','','','','1460103254','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('116','51','广东银慈会艺术团','文化广场','12','0','《广东银慈会艺术团》参赛节目是：祝福祖国。该团简介：广东银慈会艺术团于2014年初成立，属广东银慈会直接领导的艺术团体。两年来我们自编自导和改编多个舞蹈，以歌颂祖国歌颂党歌颂社会主义为主题，用舞蹈的形式积极参加社会公益演出和多次到老人院为老人演出为老人祝寿，《中国大舞台》参加中信银行主办由专家评选的大赛，得了优秀舞蹈奖，我们的热心，我们的奉献深受社会大众的欢迎得到社会各界人士的好评。','','','','','','','','','','','1460103608','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('117','51','红菱艺术团','文化广场','20','0','红菱艺术团参演的节目是多嘎多娅。节目简介：侗族是一个能歌善舞的民族，侗语：多嘎多耶即是唱起来，跳起来，唱歌跳舞的意思，侗族人每年春节冬闲时
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('118','51','天河晨运舞蹈队','文化广场','16','0','天河体育中心晨运队，我们参加表演的节目是（站在草原望北京），我们这个队成立了3年多，主要以健身为主，很少参加比赛，原因是我们的队员年龄比较大，没什么基础，就凭我自己的能力组织参加过老年协会的广场舞比赛，曾经得过第一名，第二名，这次的比赛我们不敢拿奖，志在参与，谢谢！','','','','','','','','','','','1460103666','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('119','51','致美艺术团','文化广场','14','0','致美艺术团是2O12年成立的，成员都是荔湾退管中心声乐班的学员。我们曾经参加过市、等组织的各项演出活动。获得过“美在金秋”老年人风采大赛优秀节目奖和市中老人健身排舞大赛铜奖。我们这次参演的节目扇舞《祖国颂》。我们的目标是志在参与，不断提高，尽享其乐！谢谢！','','','','','','','','','','','1460103681','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('120','51','天使舞蹈队','文化广场','18','0','《天使舞蹈团》是在广东省第二中医院郭主任的关怀下，由何护长和我成立的，队员们一部分是粉红丝带俱乐部的成员，一部分是义工。因为人们喜欢把医生和护士称作“天使”，也喜欢把心地善良的女人称作“天使”，所以就起名为《天使舞蹈团》了。愿我们《天使舞蹈团》越舞越精彩！舞出健康！舞出欢乐','','','','','','','','','','','1460103697','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('121','51','金风舞蹈队','文化广场','14','0','金凤舞蹈队创办了三年，全是退休人员组成的，都是在晚上跳广场舞。没有老师指导，都是在网上自学的。','','','','','','','','','','','1460103710','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('122','52','岭南星光体育舞蹈队','','18','0','岭南星光体育舞蹈成立于2012年，队员都是体育舞蹈者，平均年令六十岁，浪谩与激情拉丁集体舞是我们的自创节目。','','','','','','','','','','','1460104002','0','1');
