INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('124','52','沙园瑞韵舞队','','16','0','海珠区沙园街《瑞韵舞蹈队》，该队组成于2014年，队员16人现表演人数14人，平均年齡59岁，共同的爱好使大家走在一起，每天晚上大家出来跳舞，学习各种舞蹈，风雨无阻，我們秉承健康、乐观、团结、和谐的宗旨来积极参加这次活動。谢謝广州市老年文化艺术协会。','','','','','','','','','','','1460104020','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('125','52','尚姿舞艺术团','','18','0','尚姿舞艺术团成立于2013年以来，在广州市老龄办和市中老年文化艺术协会指导下，先后参加了《美在今秋》及社区组织各项公益性活动，并获得了美在金秋大赛铜奖，广州市第二届中老年舞蹈大赛金奖。我们精神宗旨是高尚、美丽、多姿多彩、舞出活力、舞出人生、舞出人间真情！','','','','','','','','','','','1460104032','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('126','52','省档案广场微笑文艺队','','14','0','广州微笑文艺队成立于2006年，队员平均年龄58岁。微笑、友爱、感恩是我们的精神宗旨！10年的努力10年的热情，让我们和全市中老年朋友一起创建和谐健康的社区生活。朋友们舞蹈起来吧！！！','','','','','','','','','','','1460104044','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('127','52','义工联艺术团','','18','0','广卅义工艺术团成立于2008年，是广卅市的一支优秀义工团队，也是最具义工精神团队。经常深入社区、养老院义演，用健康、活力、快乐的表演传播欢乐，传播正能量，深受服务对象的好评和媒体的关注，在全国、省、市的大赛中，取得优异绩。2014年、2015年都荣获广卅市老年人健身操比赛金奖;荣获中国大妈网金像奖广场舞大赛第二名;荣获广东省&quot;南粤风采&quot;艺术大赛金奖等。广卅义工艺术团具有积极，正面的社会形象，是广卅义工美的使者。','','','','','','','','','','','1460104056','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('128','52','星光艺术团','','16','0','星光艺术团成立于2005年，直接隶属越秀区星光长者联谊会文体工作部管理，是一支中老年群众文化艺术的业余团队，该团珼拥有23个艺术团队，约有400多名团员，长期活跃在广州市各个社会的文艺舞台，并在省市区文艺汇演比赛中荣获多种奖励。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('129','52','永泰娱乐健身队','','20','0','白云区永泰健身娱乐队是广州市白云恒福社会工作服务社（永平街家庭综合服务中心）注册舞蹈队伍，共50人。团队成员来自五湖四海，年龄跨度从58岁到70岁，擅长太极、太极剑、广场舞等娱乐表演。日常于永泰白云乒乓球公园和永平街家庭综合服务中心锻炼，风雨无阻，阴晴不改。队伍成员积极参与社区义工服务，热心社区公益活动，受到社区的一致好评。队伍在锻炼学习之余注重团队建设，关怀队员，发挥队伍内组员间力所能及的互助关爱精神。队伍宗旨是互相学习，老有所为老有所乐，健康长寿！','','','','','','','','','','','1460104082','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('130','52','逸景翠园舞蹈队','','18','0','逸景翠园舞蹈队属于海珠区凤阳街道逸景翠园社区的一支队伍，成立于2012年。参加这次演出的人员16人，均龄57岁。我们的宗旨是：活跃社区的文化生活，增强社区和谐，给每个队员都有机会展示自己的才华。这次比赛给了我们这个平台，向兄弟团队学习的机会。真正使我们老人们老有所为，老有所乐。使我们社区的老年朋友们夕阳更美！谢谢评审老师们！','','','','','','','','','','','1460104091','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('131','52','超越文工团','','20','0','广州超越文工团成立于2000年。是由各行各业的舞蹈爱好者共同组织起来的中老年舞蹈团队。团长李小红，现有团员56人。团员有国家机关的公务员、企事业单位的领导干部、大学教授、医护工作者、企业职员以及己移居海外的部分舞蹈爱好者等离退休人员组成。年龄最大的80岁，最小的50岁。十多年来，为使我们中老年人的生活更加丰富多彩，更加充满阳光，保持身心健康。活跃在广州市的各个舞台上。超越自我，超越梦想。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('132','53','省地质老干欢乐艺术团','','18','0','省地质局老干欢乐艺术团简介十广场舞《美丽中国》。省地质局老干欢乐艺术团成立于2O13年，是由地质局大院的退休职工组成，我们为了强身健体，丰富我们的退休生活，学习和改编了一些歌颂党，歌颂祖国和赞扬社会主义新风尚的节目参加省，市，区，的汇演。2015年参加广州老年协会举办的广场舞大赛获得了银奖。我们还积极参加公益活动，经常在社区，在养老院，及我们的活动中心义务演出，受到大家的好评。广场舞《舞动中国》诠释了我们的爱国情怀。','','','','','','','','','','','1460104135','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('133','53','富力半岛文体队','','12','0','广州市富力半岛辅导站成立在2003年12月。由居住在富力半岛花园的业主。退休闲居的居民。爱好者参与。建队是以推广全民建身。增强人民体质。推广国家运动项目。开展形式多样的文体活动。……文体队多次积极参与省市协会组改的活动。如2014年参加广卅帀銀行杯。“中老年健身排舞大赛&quot;。取得二项銀奖。同時也积极参与公益活动。到社区，亍道老人院义务演出。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('134','53','市老干瑜伽队','','20','0','由旅美华人梁樱老师执教的市老干瑜伽队，平均年龄超过60岁，长期从事老年瑜伽舞台艺术表演，她们刚从美国演出载誉归来。另类的艺术题材，惊人的技巧，令人叹服的难度以及优美的构图是这支队伍的特点，在国内外演出均获得最高荣誉。','','','','','','','','','','','1460104154','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('135','53','市电信银波艺术团','','20','0','广州电信银波艺术团简介＋舞蹈巜葵林欢歌》                                              广州电信银波艺术团成立于2005年，是由广州电信企业退休人员组成的文艺团体。建团宗旨是让退休职工在开展形式多样的文体活动中，以鲜明的时代精神和具有老年人特点的艺术形式，表达老年人生命不息，奋斗不止的激情，反映老年人快乐健康，精彩人生的乐观向上的豪情。艺术团自成立以来，多次参加了省、市组织的文艺汇演，取得可喜成绩。同时每年组织到社区街道、老人院义务演出。广州电信银波艺术团的精彩表演和积极参与公益活动，得到社会和企业集团的广泛好评，是电信企业的一张闪亮的名片，是一抹绚丽的晚霞！舞蹈《葵林欢歌》，反映广东葵乡妇女热情制作葵扇的劳动场面。该舞蹈荣获2015年第十二届《美在金秋》舞蹈大赛的银奖。','','','','','','','','','','','1460104172','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('136','53','越教轻骑艺术团','','12','0','越教轻骑舞蹈队简介；越教轻骑原是越秀区教育局关工委讲师团领导的文艺轻骑队，成立将近10年了，目的在于利用舞蹈形式协助讲师团对青少年的工作，关心下一代的成长，传播和继承祖国的传统文化。队员在发挥余热的同时得到身心愉快，增进了健康，做到老有所学，老有所为，老有所乐。文艺队大部分都是退休教师，一直以来跟随专业老师学习，不断提升舞蹈艺术水平。每年都到中小学，养老院，街道和部队慰问演出。舞蹈巜故乡是北京》是描述老北京的风情䪨味，走遍南北西东，还是最爱我的北京。象征着中华儿女热爱祖国，热爱中华文化，无论走到哪里，还是最爱自己的祖国。舞蹈以女扮男装的形式演绎。参加表演人员共10人。','','','','','','','','','','','1460104189','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('137','53','同和同心艺术团','','20','0','广州同和同心艺术团成立于2O13年，是一支年轻的队伍，基本由同和街退休人员组成。建团宗旨八个字：同心、同德、同和、同乐！三年来，多次参加了省、市组织的文艺汇演，取得了一些成绩，每年义务公益演出近40场。自编广场舞《十八湾水路到我家》，主要反映湘西姑娘出嫁前的场面和新农村新面貌的新景象！','','','','','','','','','','','1460104208','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('138','53','荔枝红艺术团','','14','0','“年轻”的老年团队，荔枝红模特艺术团，创建于2014年，成立仅一年多。队员是平均年龄58岁的知识女性。她们中有些担任过公务员处长，有教书育人的大学教师，救死扶伤的白衣天使，企业的高层管理。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('139','53','金夕阳艺术团','','12','0','广州天河金夕阳艺术团成立于2012年，是一个综合型团队，有民族乐团，京剧，舞蹈，小合唱，太极队，我们的宗旨是歌颂中国共产党的领导，宣传国家方针，政策，遵守社会道德，本团方向是丰富中老年，辖区居民文化生活，以自务自乐为主，坚持我运动，我健康，我开心、我快乐，并须参加各项公益事业演出，增强服务功能，开展扶贫助残，慈善救助宣传，养老院慰问等，履行好我们的社会职责。自建团以来，多次参加省，市，协会组织的各种益活动，参加了广东省第八届老年人运动会开幕式、2014年8月1日，在广东武警总队慰问演出，珠吉村养养老陔义演，广东省有线台演出，评为优秀节目，2015年，由省，市，协会举办的健身操，广场舞比赛获1枚铜奖，1枚优秃奖，广东省京剧票友大赛获铜奖，第十二届美在金秋综艺类获銅奖，康德养老院慰问义演，每年的五一，十一，春节在街道，社区多次演出，并获得领导观众好评，另外參加了天河区声乐比赛，获优秀奖，2016年元月19日参加了广东省电视台房产频道的录制节目，舞蹈有3个节目是自编，乐队有两首曲子是改编的，得到了专业老师认可，','','','','','','','','','','','1460104231','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('140','53','天河前进艺术团','','18','0','广卅前进艺术团成立于1998年1O月。18年来，我团坚持为人民群众服务，下农村，下基层，老人院，干休所慰问演出。对省，市，区，下达的演出任务，都圆滿完成， 受到上级领导的肯定和表杨。
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('141','53','白云老干健美舞队','','14','0','白云老干健美舞队简介: 白云老干健美舞队成立于2004年，本队以老有所乐、老有所学、老有所用为宗旨，积极开展文化娱乐活动，先后参加慰问老兵、关工委组织的“大手拉小手”活动、为老人作慰问演出、到社区巡演等活动，也获得一些荣誉，2004年参加广东省第二套健身秧歌大赛获二等奖；2013年参加第第十届“美在金秋”老年人风采大赛，本队的舞蹈《阳光路上》获优秀奖；2014年参加“美好家园杯”广州市老年人健身排舞系列综合比赛、本队的《幸福家园》获银奖，参加健身秧歌大赛、本队的《第五套健身秧歌》获二等奖，2014年，本队还参加了第八届老年人运动会开幕式的秧歌表演；2015年参加健身秧歌比赛，本队的《第五套健身秧歌》获优胜奖。','','','','','','','','','','','1460104253','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('142','54','黄埔文化馆天艺艺术团','','18','0','我们是黄埔区文化馆天艺艺术团，我们2O13年成立，现有团员2O人，平均年龄57岁。我们参赛节目《骏马欢腾》是本团自创自编的节目，展现大家用心做好自己的风格，团结健康快乐，经过努力可以做到更好的精神意境。','','','','','','','','','','','1460104350','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('143','54','岭南新世界夕阳红艺术团','','18','0','夕阳红艺术团成立2OO5年5月己整整有1O周年。下设有舞蹈队12人;腰鼓队3O人;合唱队4o人;乐队8人。这四个队经常去参加公益的大型活动，曾经参加亚运.粵运会，腰鼓展演，参加广东省第八届老人运动会开幕试等。参加15年1O月教师节期间，参加大妈广场舞大赛荣获第二名，获得奖金15o元，创作奖5oo元。','','','','','','','','','','','1460104361','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('144','54','韵舞飞扬舞蹈队','','20','0','我们是天河区棠下街天安社区韵舞飞杨舞蹈队。建队几年来，多次参加市、区、街道及有关部门组织的比赛和义演。曾荣获二等奖、三等奖、优秀奖及最佳风彩奖，并上了广州电视。我们队主要是以广场舞，民族舞为主。为锻炼身体，增强体质而展示快乐老人的风彩。','','','','','','','','','','','1460104370','0','1');
