INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('146','54','东风公园舞蹈队','','14','0','东风舞蹈队，我们的舞蹈队是在2003年组建。历经13年之久！舞蹈队以广场舞为主，多次去慰问演出，做善事。我们的舞姿都得到许多有关人士的认可。这次的演出是串舞。请你们多多指教','','','','','','','','','','','1460104389','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('147','54','长湴长虹艺术团','','20','0','','','','','','','','','','','','1460104402','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('148','54','广州老干部艺术团','','14','0','广州老干部艺术团，成立21年，现有团员40人（男17人，女23人）我团的节目舞种多，一直坚持以原创为主。其中拉丁编队舞巜我们的节日》、 西班牙巜斗牛舞》、情景舞巜我爱我家》、民族舞巜生命探戈》巜葡萄园的姑娘》等先后参加省、市等中老年舞蹈大赛均获金，银等奖项多次。其中巜我爱我家》于去年4月参加了中央3台巜舞蹈世界》栏目的演出！','','','','','','','','','','','1460104422','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('149','54','萝岗东区幸福街坊长者健身队','','12','0','罗岗东区幸福长者健身队成立3年多，队员都是农转居民的老年人，以前农村生活没有文化生活的时间，更淡不上上舞台表演。现在社会结构变了，我们都有一份微弱的养老金，生活之余也隨城里大妈跳起了广场舞。大家很开心，很快乐！','','','','','','','','','','','1460104971','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('150','54','黄埔文化馆同心艺术团','','20','0','黄埔区文化馆同心艺术团，是文化馆直接领导及辅导的一支队伍，成立于2006年，从当初的六个人，发展到现在的二十人，在成立的十年间里，她们以极大的热情送戏下乡，下工厂，进社区，到敬老院，辅导民工基层文艺队伍，与民同乐，共演出了300多场，为宣传党的方针政策，服务于广大人民群众，活跃社区文化，作出了很大的贡献，受到区内领导的多次表彰。她们老有所乐，老有所为，对艺术精益求精，使身体更健康，精神更煥发，节目水平更加不断提高，她们每年参加广卅市和区内各类大型的艺术比赛，获得了很多的奖项，受到领导与群众的一致好评。请看群舞&lt;张灯结彩&gt;。','','','','','','','','','','','1460104983','0','1');
INSERT INTO jc_contingent ( `id`, `location_id`, `name`, `community_name`, `num_competition`, `age`, `resume`, `content_1`, `content_2`, `content_3`, `content_4`, `content_5`, `content_pic_1`, `content_pic_2`, `content_pic_3`, `content_pic_4`, `content_pic_5`, `time`, `sort`, `is_effect` ) VALUES  ('151','54','梦之舞艺术团','','14','0','越秀梦之队，成立于2011年5月，2016年1月更名为《越秀梦之舞艺术团》，是一支平均年龄60岁左右的中老年舞蹈团。舞蹈团主要以民族舞和广场舞为主，队员从零开始自编自学舞蹈。接近6年的时间，舞蹈队由刚开始几个人发展成至今已有30人的团体。舞蹈团驻扎在启创北京街家庭综合服务中心，逢周三及周五下午定期在中心进行日常训练和排练节目。
